import React, { useState } from 'react';

export const SavingsForm = () => {
  const [userInput, setUserInput] = useState({
    'current-savings': 0,
    'yearly-contribution': 0,
    'expected-return': 0,
    duration: 0
  });

  const inputOnChangeHandler = (inputId, value) => {
    setUserInput((prevState) => ({
      ...prevState,
      [inputId]: value
    }));
  };

  const calculateHandler = (userInputEvent) => {
    // Preventing browser to submit and refresh the page
    userInputEvent.preventDefault();

    const yearlyData = []; // per-year results

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      // currentSavings += yearlyInterest + yearlyContribution;
      setCurrentSaving((prevState) => prevState + yearlyInterest + yearlyContribution);
      yearlyData.push({
        year: i + 1,
        yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution
      });
    }

    console.log(yearlyData);
    // do something with yearlyData ...
  };

  return (
    <div>
      <form className="form" onSubmit={calculateHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input
              type="number"
              id="current-savings"
              onChange={(event) => inputOnChangeHandler('current-savings', event.target.value)}
            />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" name="yearly-contribution" />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">Expected Interest (%, per year)</label>
            <input type="number" id="expected-return" name="expected-return" />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" name="duration" />
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    </div>
  );
};
